import React, { useState } from 'react';
import './Registro.css';

export default function Registro() {
    const [formData, setFormData] = useState({
        user: "",
        rut: "",
        email: "",
        region: "",
        comuna: "",
        password: "",
        verificarpassword: "",
        terminos: false
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prevState => ({
            ...prevState,
            [name]: value
        }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        // Validaciones aquí
        if (formData.user.length < 5) {
            alert("El nombre de usuario debe tener al menos 5 caracteres.");
        }
    };

    return (
        <div>
            <form onSubmit={handleSubmit} id="formulario" method="get">
                <label className='labelSign' htmlFor="user">Nombre de usuario</label>
                <input
                    className='inputSign'
                    type="text"
                    id="user"
                    name="user"
                    placeholder="Nombre de Usuario"
                    value={formData.user}
                    onChange={handleChange}
                /><br /><br />
                {/* Resto de los campos de entrada */}
                <div className='buttonSignDiv'>
                    <input type="submit" value="Registrarme" id="button" />
                </div>
            </form>
        </div>
    );
}
