import React, { useState } from 'react';
import './Registro.css';

export default function Registro() {
    const [formData, setFormData] = useState({
        user: "",
        rut: "",
        email: "",
        region: "",
        comuna: "",
        password: "",
        verificarpassword: "",
        terminos: false
    });

    const handleChange = (e) => {
        const { name, value, type, checked } = e.target;
        setFormData(prevState => ({
            ...prevState,
            [name]: type === 'checkbox' ? checked : value
        }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        // Validaciones aquí
        if (formData.user.length < 5) {
            alert("El nombre de usuario debe tener al menos 5 caracteres.");
        } else if (formData.password.length < 8) {
            alert("La contraseña debe tener al menos 8 caracteres.");
        } else if (!formData.email.includes("@")) {
            alert("Ingrese un correo electrónico válido.");
        } else if (formData.password !== formData.verificarpassword) {
            alert("Las contraseñas no coinciden.");
        } else if (!formData.terminos) {
            alert("Debe aceptar los términos y condiciones.");
        } else {
            // Si todas las validaciones son exitosas, puedes realizar acciones adicionales o enviar los datos a un servidor.
            console.log("Formulario enviado:", formData);
        }
    };

    return (
        <div className="form-container">
            <form onSubmit={handleSubmit} id="formulario" method="get" className="form">
                <label className='labelSign' htmlFor="user">Nombre de usuario</label>
                <input className='inputSign' type="text" id="user" name="user" placeholder="Nombre de Usuario" onChange={handleChange} value={formData.user} /><br /><br />

                {/* Resto de los campos de entrada */}

                <input type="submit" value="Registrarme" id="button" />
            </form>
        </div>
    );
}
